import {NextFunction} from "express";
import {OrderModel} from "../models/order.model";
import {Master, MasterBusyDate, Order, User} from "../models";
import PDFDocument from 'pdfkit';
import {Base64Encode} from 'base64-stream';
import * as fs from "fs";
import ApiError from "../exeptions/api-error";
import qrService from "./qrService";
import {MasterBusyDateModel} from "../models/masterBusyDate.model";
import {UserModel} from "../models/user.model";
import {MasterModel} from "../models/master.model";
import {v4 as uuidv4} from "uuid";
import path from "path";

interface OrderWithMasterBusyDateUserAndMaster extends OrderModel {
    master_busyDate: MasterBusyDateModel
    user: UserModel
    master: MasterModel
}

class PdfService {
    async createPdf(orderId: number, next: NextFunction): Promise<string> {
        return new Promise(function (resolve, reject) {
                Order.findOne({
                    where: {id: orderId},
                    include: [{model: MasterBusyDate}, {model: User}, {model: Master}]
                })// @ts-ignore
                    .then((order: OrderWithMasterBusyDateUserAndMaster) => {
                            if (!order) reject(ApiError.BadRequest(`Order is not found`))
                            if (order) {
                                qrService.createQrCode({id: order.id,
                                    dateTime: order.master_busyDate.dateTime,
                                    clockSize: order.clockSize,
                                    city: order.originalCityName,
                                    userEmail: order.user.email,
                                    masterEmail: order.master.email,
                                    totalPrice: order.totalPrice,
                                    next: next}).then((qrCodeBase64) => {
                                    if (!qrCodeBase64) reject(ApiError.BadRequest(`Problem with generating qr code`))
                                    /*const fontDirectory: string = path.resolve(__dirname, '..', 'fonts', 'Oswald-SemiBold.ttf')*/

                                    const doc = new PDFDocument({size: 'A4'});
                                    let finalString = ''
                                    let stream = doc.pipe(new Base64Encode())

                                    const logo = 'data:image/png;base64,'
                                    doc.image(logo, 100, 40, {width: 100, height: 100})

                                    doc.fontSize(27)
                                        /*.font(fontDirectory)*/
                                        .text(`Clock repair company`, 220, 70);

                                    doc.fontSize(14)
                                        .text(`Order number: ${order.id}`, 100, 170);
                                    doc.fontSize(14)
                                        .text(`Date: ${new Date(order.master_busyDate.dateTime).toLocaleString()}`, 100, 200);
                                    doc.fontSize(14)
                                        .text(`Clock size: ${order.clockSize === 1 ? 'small' : order.clockSize === 2 ? 'middle' : 'big'}`, 100, 230);
                                    doc.fontSize(14)
                                        .text(`City name: ${order.originalCityName}`, 100, 260);
                                    doc.fontSize(14)
                                        .text(`User email: ${order.user.email}`, 100, 290);
                                    doc.fontSize(14)
                                        .text(`Master email: ${order.master.email}`, 100, 320);
                                    doc.fontSize(14)
                                        .text(`Total price: ${order.totalPrice} USD`, 100, 350);

                                    doc.image(qrCodeBase64, 230, 580, {width: 150, height: 150})

                                    doc.fillColor('red')
                                        .text('Our web site', 270, 740)
                                        .link(270, 740, 160, 27, `${process.env.CLIENT_URL}`);

                                    doc.end();
                                    stream.on('data', function (chunk) {
                                        finalString += chunk;
                                    });
                                    stream.on('end', function () {
                                        resolve(finalString)
                                    });
                                })
                            }
                        }
                    )

            }
        )
    }
}


export default new PdfService();

